	  $qb = $this
	    ->createQueryBuilder('c')
	    ->Join('c.commercial', 'com')
	    ->addSelect('com.nomContact')
	    ->Join('c.client', 'cli');

	  return $qb
	    ->getQuery()
	    ->getResult();
	}

	public function getCommandeParSociete($id)
	{

	  $qb = $this
	    ->createQueryBuilder('c')
	    ->Join('c.commercial', 'com')
	    ->addSelect('com.nomContact')
	    ->Join('c.client', 'cli')
	    ->where('cli.idtSociete = :id')
	    ->setParameters(array('id'=> $id));

	  return $qb
	    ->getQuery()
	    ->getResult();
	}
}
